import React, { useState } from "react";

function UpdateRating({ ratingId, onUpdate }) {
  const [username, setUsername] = useState("");
  const [artist, setArtist] = useState("");
  const [song, setSong] = useState("");
  const [rating, setRating] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUsername(localStorage.getItem("user"));
    try {
      let res = await fetch("http://localhost/COMP333_HW3/index.php/updaterating", {
        method: "POST",
        body: JSON.stringify({
          id: ratingId,
          username,
          artist,
          song,
          rating,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      });
      let resJson = await res.json();
      if (res.status === 200) {
        onUpdate({ artist, song, rating }); // Update the state immediately
        setUsername("");
        setArtist("");
        setSong("");
        setRating("");
        setMessage("Rating updated");
      } else if (res.status === 400) {
        setMessage(resJson.error);
      } else {
        setMessage("Something went wrong");
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="UpdateRating">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={artist}
          placeholder="Artist"
          onChange={(e) => setArtist(e.target.value)}
        />
        <input
          type="text"
          value={song}
          placeholder="Song"
          onChange={(e) => setSong(e.target.value)}
        />
        <input
          type="text"
          value={rating}
          placeholder="Rating"
          onChange={(e) => setRating(e.target.value)}
        />
        <button type="submit">Update rating</button>
        <div className="message">{message ? <p>{message}</p> : null}</div>
      </form>
    </div>
  );
}

export default UpdateRating;
