import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import './App.css';
import Ratings from './ratingstable';
import LoginUser from './loginuser';
import CreateUser from './createuser';
import AddNewRating from './addnewrating';
import ViewRating from './viewrating';
import Filters from './searchfilterratings'; 

function App() {
  const [user, setUser] = useState(""); // To store the logged-in user
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the user is logged in
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      setUser(loggedInUser);
      setLoggedIn(true);
    }
  }, []);

  const handleLogin = (username) => {
    // Update the user state and set logged in status
    setUser(username);
    setLoggedIn(true);
    // Store the user in local storage for persistence
    localStorage.setItem("user", username);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(""); // Clear the user state
    setLoggedIn(false); // Update the login status
  };

  return (
    <Router>
      <div>
        <div className="app-container">
          <div className="main-content">
            <Routes>
              <Route path="/ratingstable" element={<Ratings />} />
              <Route path="/searchfilterratings" element={<Filters />} /> 
            </Routes>
          </div>
          <div className="sidebar-right">
            {!loggedIn ? (
              <div className="login_signin">
                <div className="login">
                  <strong>Log In</strong>
                  <LoginUser onLogin={handleLogin} />
                </div>

                <div className="signin">
                  <strong>Sign Up</strong>
                  <CreateUser onSignUp={handleLogin} />
                </div>
              </div>
            ) : (
              <div>
                <button className="exit-button" onClick={handleLogout}>
                  Exit
                </button>
                <div className="add-song-rating">
                  <strong>Add New Song Rating</strong>
                  <AddNewRating />
                </div>
              </div>
            )}
          </div>
        </div>

        <hr />
      </div>
    </Router>
  );
}

export default App;
