import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Outlet } from "react-router-dom";
import './App.css';
import Ratings from './ratingstable';
import LoginUser from './loginuser';
import CreateUser from './createuser';
import AddNewRating from './addnewrating';

function App() {
  const [user, setUser] = useState(""); // To store the logged-in user
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the user is logged in
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      setUser(loggedInUser);
      setLoggedIn(true);
    }
  }, []);

  const handleLogin = (username) => {
    // Update the user state and set logged in status
    setUser(username);
    setLoggedIn(true);
    // Store the user in local storage for persistence
    localStorage.setItem("user", username);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(""); // Clear the user state
    setLoggedIn(false); // Update the login status
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            {user ? (
              <>
                <li><Link to="/ratings">Ratings</Link></li>
                <li><Link to="/addnew">Add New Song</Link></li>
                <li><button onClick={handleLogout}>Exit</button></li>
              </>
            ) : (
              <>
                <li><Link to="/login">Log In</Link></li>
                <li><Link to="/signup">Sign Up</Link></li>
              </>)
            }
          </ul>
        </nav>

        <Routes>
          <Route path="/login" element={<LoginUser onLogin={handleLogin} />} />
          <Route path="/signup" element={<CreateUser onSignUp={handleLogin} />} />
          <Route
            path="/"
            element={
              loggedIn ? (
                <Outlet>
                  <Route path="/ratings" element={<Ratings />} />
                  <Route path="/addnew" element={<AddNewRating />} />
                </Outlet>
              ) : null
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
