import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import './App.css';
import Ratings from './ratingstable';
import CreateUser from './createuser';
import LoginUser from './loginuser';
import AddNewRating from './addnewrating';
import ViewRating from './viewrating';
import Filters from './searchfilterratings';

function App() {
  const [user, setUser] = useState(""); // To store the logged-in user
  const [loggedIn, setLoggedIn] = useState(false);

  // State variable to handle redirection after login
  const [redirectAfterLogin, setRedirectAfterLogin] = useState(false);

  useEffect(() => {
    // Check if the user is logged in
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      setUser(loggedInUser);
      setLoggedIn(true);
    }
  }, []);

  const handleLogin = (username) => {
    // Update the user state and set logged in status
    setUser(username);
    setLoggedIn(true);
    // Store the user in local storage for persistence
    localStorage.setItem("user", username);

    // Set the state variable to trigger redirection
    setRedirectAfterLogin(true);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(""); // Clear the user state
    setLoggedIn(false); // Update the login status
    // Redirect to the login page after logout
    setRedirectAfterLogin(true);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/ratingstable">Ratings</Link>
            </li>
            {loggedIn ? (
              <>
                <li>
                  <Link to="/addnewrating">Add New Song</Link>
                </li>
                <li>
                  <button onClick={handleLogout}>Exit</button>
                </li>
              </>
            ) : null}
          </ul>
        </nav>
        <Routes>
          <Route path="/ratingstable" element={<Ratings />} />
          <Route path="/searchfilterratings" element={<Filters />} />
          {loggedIn ? (
            <Route path="/addnewrating" element={<AddNewRating />} />
          ) : null}
        </Routes>
      </div>
      <hr />
      <Routes>
        <Route
          path="/login"
          element={redirectAfterLogin ? <Navigate to="/ratingstable" /> : <LoginUser onLogin={handleLogin} />}
        />
        <Route
          path="/signup"
          element={redirectAfterLogin ? <Navigate to="/ratingstable" /> : <CreateUser onSignUp={handleLogin} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
