import React, { useState, useEffect } from "react";

function Filters() {
  const [ratings, setRatings] = useState([]);
  const [searchText, setSearchText] = useState("");
  const [singerFilter, setSingerFilter] = useState("");
  const [ratingFilter, setRatingFilter] = useState("");

  const fetchData = async () => {
    // Fetch data from the backend without any filtering
    try {
      const response = await fetch("http://localhost/COMP333_HW3/index.php/getratings");
      if (response.ok) {
        const data = await response.json();
        setRatings(data);
      } else {
        console.error("Failed to fetch data");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const applyFilters = () => {
    // Create a copy of the original data to apply filters
    let filteredData = [...ratings];

    // Apply the search filter (song name and singer)
    filteredData = filteredData.filter((rating) => {
      return (
        rating.song.toLowerCase().includes(searchText.toLowerCase()) ||
        rating.singer.toLowerCase().includes(searchText.toLowerCase())
      );
    });

    // Apply the singer filter
    if (singerFilter) {
      filteredData = filteredData.filter((rating) =>
        rating.singer.toLowerCase().includes(singerFilter.toLowerCase())
      );
    }

    // Apply the rating filter
    if (ratingFilter) {
      filteredData = filteredData.filter(
        (rating) => rating.rating === parseInt(ratingFilter)
      );
    }

    setRatings(filteredData);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search by Song Name or Singer"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by Singer"
          value={singerFilter}
          onChange={(e) => setSingerFilter(e.target.value)}
        />
        <input
          type="number"
          placeholder="Filter by Rating"
          value={ratingFilter}
          onChange={(e) => setRatingFilter(e.target.value)}
        />
        <button onClick={applyFilters}>Apply Filters</button>
      </div>
      <div>
        {/* Display the list of ratings based on the filtered data */}
        {ratings.map((rating) => (
          <div key={rating.id}>
          <p>Song: {rating.song}</p>
          <p>Singer: {rating.singer}</p>
          <p>Rating: {rating.rating}</p>
        </div>
        ))}
      </div>
    </div>
  );
}

export default Filters;
