import React, { useState } from "react";
import axios from "axios";

function LoginUser() {


    const [data, setData] = useState([]);

    useEffect(() => {
      axios
        .post("http://localhost/COMP333_HW3/index.php/loginuser", {
            username,
            password
        })
        .then((response) => {
          setData(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);


    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const submit = async () => {
        let res = await axios.post("http://localhost/COMP333_HW3/index.php/loginuser", {
            username,
            password
        }
        );
    };
    axios.then(res => this.setState({ login : res.data}));
    axios.catch(err => console.log(err));

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={submit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
    </div>
  );


