import React, { useState, useEffect } from "react";
import axios from "axios";

function Ratings() {
  // State to hold the ratings data
  const [ratings, setRatings] = useState([null]);

  // Fetch ratings data from the API
  useEffect(() => {
    axios.get("http://localhost/COMP333_HW3/index.php/ratings")
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        setRatings(data)
      })
      .catch((error) => {
        console.error("Error fetching ratings:", error);
      });
  }, []);

  return (
    <div className="ratings-table">
      <h1>Ratings</h1>
      <ul>
        {ratings.map((rating) => (
          <div className="ratings-preview" key={ratings.id}>
            <h2>{ratings.song}</h2>
            <p>by {ratings.artist}</p>
            <p>rated by {ratings.username}</p>
            <p>rating: {ratings.rating}</p>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default Ratings;
